        mrechange:function(){
            var emailstart = $(".switch_email_f");
            var phonestart = $(".switch_phone_f");
            var echange = $(".switch_phone_t");
            var pchange = $(".switch_email_t");
            var conemail = $(".part2_m");
            var conphone = $(".part1_m");
            emailstart.click(function(){
                conemail.show();
                conphone.hide();
                emailstart.hide();
                echange.hide();
                phonestart.show();
                pchange.show();
            });
            phonestart.click(function(){
                conemail.hide();
                conphone.show();
                emailstart.show();
                echange.show();
                phonestart.hide();
                pchange.hide();
            });
        },

        regchange:function(){
            var pt = $(".select_wrap_p1");
            var ef = $(".select_wrap_e2");
            var pf = $(".select_wrap_p2");
            var et = $(".select_wrap_e1");
            var ca = $(".registered_change_01");
            var cb = $(".registered_change_02");
            ef.click(function(){
                pt.hide();
                ef.hide();
                ca.hide();
                cb.show();
                pf.show();
                et.show();
            });
            pf.click(function(){
                pt.show();
                ef.show();
                ca.show();
                cb.hide();
                pf.hide();
                et.hide();
            });
        },

        registered_agree:function(){
            var yes = $(".chose");
            var no = $(".nochose");
            yes.click(function(){
                yes.hide();
                no.show();
            });
            no.click(function(){
                no.hide();
                yes.show();
            });
        },

        popupbox:function(){
            var popstart = $(".summer_intobotton");
            var popshowf = $(".popup_back");
            var popshows = $(".popup_wrap");
            var popclose = $(".popup_out");
            popstart.click(function(){
                popshowf.show();
                popshows.show();
            });
            popshowf.click(function(){
                popshowf.hide();
                popshows.hide();
            });
            popclose.click(function(){
                popshowf.hide();
                popshows.hide();
            });
        },
    wxShares_func: function() {
        var shareUrl = "http://wx.locojoy.com/oauth/iSignatureV1"; //微信接口用

        var _this = this;
        var myLink = encodeURIComponent(encodeURIComponent(window.location.href)); //"http://"+location.host+location.pathname;//"http://mtzqdn.locojoy.com/huodong1.html";
        $.ajax({
            type: "GET",
            url: shareUrl,
            crossDomain: true,
            dataType: "jsonp",
            data: "&url=" + myLink,
            success: function(rst) {
                if (rst['error']) { //请求的数据返回错误的时候
                    alert(rst['error']);
                } else {
                    var data = rst["data"];
                    share_api(data);
                };
            }
        });

        function share_api(data) {
            wx.config({
                debug: false, // 开启调试模式,调用的所有api的返回值会在客户端alert出来，若要查看传入的参数，可以在pc端打开，参数信息会通过log打出，仅在pc端时才会打印。
                appId: data["appid"], // 必填，公众号的唯一标识
                timestamp: data["timestamp"], // 必填，生成签名的时间戳
                nonceStr: data["noncestr"], // 必填，生成签名的随机串
                signature: data["signature"], // 必填，签名，见附录1
                jsApiList: [
                        'checkJsApi',
                        'onMenuShareTimeline',
                        'onMenuShareAppMessage',
                        'onMenuShareQQ',
                        'onMenuShareWeibo',
                        'hideMenuItems',
                        'showMenuItems',
                        'hideAllNonBaseMenuItem',
                        'showAllNonBaseMenuItem',
                        'translateVoice',
                        'startRecord',
                        'stopRecord',
                        'onRecordEnd',
                        'playVoice',
                        'pauseVoice',
                        'stopVoice',
                        'uploadVoice',
                        'downloadVoice',
                        'chooseImage',
                        'previewImage',
                        'uploadImage',
                        'downloadImage',
                        'getNetworkType',
                        'openLocation',
                        'getLocation',
                        'hideOptionMenu',
                        'showOptionMenu',
                        'closeWindow',
                        'scanQRCode',
                        'chooseWXPay',
                        'openProductSpecificView',
                        'addCard',
                        'chooseCard',
                        'openCard'
                    ] // 必填，需要使用的JS接口列表，所有JS接口列表见附录2
            });

            wx.ready(function() {
                shareContent();
            });

            function shareContent() {
                wx.onMenuShareTimeline({
                    title: '',
                    link: '',
                    imgUrl: '',
                    trigger: function(res) { //用户点击分享到朋友圈

                    },
                    success: function(res) { //用户确认分享后执行的回调函数

                    },
                    cancel: function(res) { //用户取消分享后执行的回调函数

                    },
                    fail: function(res) {
                        alert(JSON.stringify(res));
                    }
                });
                wx.onMenuShareAppMessage({
                    title: '', // 分享标题
                    desc: '', // 分享描述
                    link: '', // 分享链接
                    imgUrl: '', // 分享图标
                    type: '', // 分享类型,music、video或link，不填默认为link
                    dataUrl: '', // 如果type是music或video，则要提供数据链接，默认为空
                    success: function() {
                        // 用户确认分享后执行的回调函数
                    },
                    cancel: function() {
                        // 用户取消分享后执行的回调函数
                    }
                });

                var shareData = {
                    title: "酷爽夏日，浪漫七夕——首届《我叫MT》夏日狂欢节",
                    link: data["url"],
                    imgUrl: "http://resource.locojoy.com/mt/images/chanpin/gongsi/p20150817gome/share.jpg"
                };
                var shareDataFriend = {
                    title: "酷爽夏日，浪漫七夕",
                    desc: "酷爽夏日，浪漫七夕——首届《我叫MT》夏日狂欢节",
                    link: data["url"],
                    imgUrl: "http://resource.locojoy.com/mt/images/chanpin/gongsi/p20150817gome/share.jpg"
                };
                wx.onMenuShareTimeline(shareData);
                wx.onMenuShareAppMessage(shareDataFriend);
            };
        };
    }
