function mouse_over_style1(ele1, ele2) {
    ele1.hover(function() {
        ele2.css({
            display: "block"
        });
        setTimeout(function() {
            ele2.addClass("show");
        }, 10);
    }, function() {
        ele2.removeClass("show");
        setTimeout(function() {
            ele2.css({
                display: "none"
            });
        }, 200);
    });
}
