;
(function($) {
	$.fn.numberAnimate = function(setting) {
		var defaults = {
			speed: 1000,
			num: "",
			iniAnimate: true,
			symbol: '',
			dot: 0
		};
		var setting = $.extend(defaults, setting);
		if($(this).length > 1) {
			alert("just only one obj!");
			return
		}
		if(setting.num == "") {
			alert("must set a num!");
			return
		}
		var nHtml = '<li class="mt-number-animate-dom" data-num="{{num}}">\
			            <span class="mt-number-animate-span">0</span>\
			            <span class="mt-number-animate-span">1</span>\
			            <span class="mt-number-animate-span">2</span>\
			            <span class="mt-number-animate-span">3</span>\
			            <span class="mt-number-animate-span">4</span>\
			            <span class="mt-number-animate-span">5</span>\
			            <span class="mt-number-animate-span">6</span>\
			            <span class="mt-number-animate-span">7</span>\
			            <span class="mt-number-animate-span">8</span>\
			            <span class="mt-number-animate-span">9</span>\
		          	</li>';
		var numToArr = function(num) {
			//			num = parseFloat(num).toFixed(setting.dot);
			if(typeof(num) == 'number') {
				var arrStr = num.toString().split("");
			} else {
				var arrStr = num.split("");
			}
			return arrStr
		};
		var setNumDom = function(arrStr) {
			var shtml = '<ul class="mt-number-animate yy_number">';
			for(var i = 0, len = arrStr.length; i < len; i += 1) {
				if(i != 0 && (len - i) % 3 == 0 && setting.symbol != "" && arrStr[i] != ".") {
					shtml += '<div class="mt-number-animate-dot">' + setting.symbol + '</div>' + nHtml.replace("{{num}}", arrStr[i])
				} else {
					shtml += nHtml.replace("{{num}}", arrStr[i])
				}
			}
			shtml += '</ul>';
			return shtml
		};
		var runAnimate = function($parent) {
			$parent.find(".mt-number-animate-dom").each(function() {
				var self = $(this);
				var num = $(this).attr("data-num");
				num = (num == "." ? 10 : num);
				var spanHei = 105;
				var thisTop = -num * spanHei + "px";
				if(thisTop != $(this).css("top")) {
					if(setting.iniAnimate) {
						if(!window.applicationCache) {
							$(this).animate({
								top: thisTop
							}, setting.speed)
						} else {
							if($(".yy_number li").length == 1) {
								$(".yy_number li:nth-child(1)").css("margin-left", "525px");
							} else if($(".yy_number li").length == 2) {
								$(".yy_number li:nth-child(1)").css("margin-left", "448px");
							} else if($(".yy_number li").length == 3) {
								$(".yy_number li:nth-child(1)").css("margin-left", "350px");
							} else if($(".yy_number li").length == 4) {
								$(".yy_number li:nth-child(1)").css("margin-left", "263px");
							} else if($(".yy_number li").length == 5) {
								$(".yy_number li:nth-child(1)").css("margin-left", "177px");
							} else if($(".yy_number li").length == 6) {
								$(".yy_number li:nth-child(1)").css("margin-left", "90px");
							} else if($(".yy_number li").length == 7) {
								$(".yy_number li:nth-child(1)").css("margin-left", "3px");
							}
							setTimeout(function() {
								self.css({
									'transform': 'translateY(' + thisTop + ')',
									'-ms-transform': 'translateY(' + thisTop + ')',
									'-moz-transform': 'translateY(' + thisTop + ')',
									'-webkit-transform': 'translateY(' + thisTop + ')',
									'-o-transform': 'translateY(' + thisTop + ')',
									'-ms-transition': setting.speed / 1000 + 's',
									'-moz-transition': setting.speed / 1000 + 's',
									'-webkit-transition': setting.speed / 1000 + 's',
									'-o-transition': setting.speed / 1000 + 's',
									'transition': setting.speed / 1000 + 's'
								})
							}, 1000);
						}
					} else {
						setting.iniAnimate = true;
						$(this).css({
							top: thisTop
						})
					}
				}
			})
		};
		var init = function($parent) {
			$parent.html(setNumDom(numToArr(setting.num)));
			runAnimate($parent)
		};
		this.resetData = function(num) {
			var newArr = numToArr(num);
			var $dom = $(this).find(".mt-number-animate-dom");
			if($dom.length < newArr.length) {
				$(this).html(setNumDom(numToArr(num)))
			} else {
				$dom.each(function(index, el) {
					$(this).attr("data-num", newArr[index])
				})
			}
			runAnimate($(this))
		};
		init($(this));
		return this
	}
})(jQuery);